mapper.select:
/* bannerCnt 배너 리스트 갯수 조회  */
SELECT	COUNT(*) CNT
FROM	BANNER

mapper.sql:
/* bannerWhere 배너 조회조건  */


		AND 	BANNER_ID LIKE '%' || #{bannerId} || '%'


		AND 	BANNER_TITLE LIKE '%' || #{bannerTitle} || '%'


		AND 	USE_YN = #{useYn}

mapper.insert:
/* saveBanner 배너 저장 (신규, 수정)  */
MERGE
INTO	BANNER B
USING	DUAL
ON 		(B.BANNER_ID = #{bannerId})
WHEN 	MATCHED THEN
UPDATE
SET		BANNER_TITLE	= #{bannerTitle}
,		BANNER_STATUS	= #{bannerStatus}
,		BANNER_CONTENT	= #{bannerContent}
,		BANNER_IMAGE_NO	= #{bannerImageNo}
,		USE_YN			= #{useYn}
,		UPDATE_DATE		= #{updateDate}
,		UPDATE_USER		= #{updateUser}
WHEN 	NOT MATCHED THEN
INSERT
(
	BANNER_ID
,	BANNER_TITLE
,	BANNER_STATUS
,	BANNER_CONTENT
,	BANNER_IMAGE_NO
,	USE_YN
,	CREATE_DATE
,	CREATE_USER
,	UPDATE_DATE
,	UPDATE_USER
)
VALUES
(
	(
		SELECT	NVL(MAX(BANNER_ID), 0) + 1
		FROM	BANNER
	)
,	#{bannerTitle}
,	#{bannerStatus}
,	#{bannerContent}
,	#{bannerImageNo}
,	#{useYn}
,	#{createDate}
,	#{createUser}
,	#{updateDate}
,	#{updateUser}
)

mapper.delete:
/* deleteBanner 배너 삭제  */
DELETE
FROM	BANNER
WHERE 	BANNER_ID IN

	#{idx}
