mapper.select:
/* codeCnt 코드 리스트 갯수 조회  */
SELECT	COUNT(*) CNT
FROM	CODE

mapper.sql:
/* codeWhere 코드 조회조건  */


		AND 	CODE_ID LIKE '%' || #{codeId} || '%'


		AND 	CODE_NAME LIKE '%' || #{codeName} || '%'


		AND 	USE_YN = #{useYn}

mapper.insert:
/* saveCode 코드 저장 (신규, 수정)  */
MERGE
INTO	CODE C
USING	DUAL
ON 		(C.CODE_ID = #{idx})
WHEN 	MATCHED THEN
UPDATE
SET		CODE_NAME			= #{codeName}
,		UPPER_CODE_ID		= #{upperCodeId}
,		CODE_VALUE			= #{codeValue}
,		USE_YN				= #{useYn}
,		DELETED				= #{deleted}
,		CODE_INFO			= #{codeInfo}
,		UPDATE_USER			= #{updateUser}
,		UPDATE_DATE			= #{updateDate}
WHEN 	NOT MATCHED THEN
INSERT
(
	CODE_ID
,	CODE_NAME
,	UPPER_CODE_ID
,	CODE_VALUE
,	USE_YN
,	DELETED
,	CODE_INFO
,	CREATE_USER
,	CREATE_DATE
,	UPDATE_USER
,	UPDATE_DATE
)
VALUES
(
	(
	SELECT	NVL(MAX(CODE_ID),0)+1
	FROM	CODE
	)
,	#{codeName}
,	#{upperCodeId}
,	#{codeValue}
,	#{useYn}
,	#{deleted}
,	#{codeInfo}
,	#{createUser}
,	#{createDate}
,	#{updateUser}
,	#{updateDate}
)

mapper.delete:
/* deleteCode 코드 저장 (신규, 수정)  */
DELETE
FROM	CODE
WHERE 	CODE_ID IN

	#{idx}
