mapper.insert:
/* fileUpload 업로드파일 정보 저장 */
INSERT
INTO	FILE_INFO
(
	FILE_NO
,	FILE_PATH
,	FILE_NAME
,	FILE_PHYSICAL_NAME
,	FILE_EXTEN
,	FILE_SIZE
,	USE_YN
,	DELETED
,	CREATE_USER
,	CREATE_DATE
,	UPDATE_USER
,	UPDATE_DATE
)	VALUES
(
	#{fileNo}
,	#{filePath}
,	#{fileName}
,	#{filePhysicalName}
,	#{fileExten}
,	#{fileSize}
,	#{useYn}
,	#{deleted}
,	#{createUser}
,	#{createDate}
,	#{updateUser}
,	#{updateDate}
)

mapper.select:
/* createfileNo fileMaxNo를 생성한다.  */
SELECT	NVL(MAX(FILE_NO),0) +1  AS CNT
FROM	FILE_INFO
