mapper.select:
/* loginProcessing 로그인 결과 조회 */
	SELECT	PASSWORD
	FROM	USER_INFO
	WHERE 	USER_ID = #{userId}

mapper.insert:
/* insertUser 계정 생성 */
	INSERT
	INTO	USER_INFO
	(		USER_ID
	,		PASSWORD
	,		USER_NAME
	,		AGE
	,		GENDER
	,		EMAIL
	,		LOCATION
	,		LOCATION_DTL
	,		PHONE_NUM
	,		IMAGE_FILE_NO
	,		USER_URLE
	,		DELETED
	,		CREATE_USER
	,		UPDATE_USER
	,		CREATE_DATE
	,		UPDATE_DATE
	)		VALUES
	(		#{userId}
	,		#{password}
	,		#{userName}
	,		#{age}
	,		#{gender}
	,		#{email}
	,		#{location}
	,		#{locationDetail}
	,		#{phoneNum}
	,		#{imageFileNo}
	,		#{rule}
	,		'0'
	,		#{createUser}
	,		#{updateUser}
	,		SYSDATE
	,		SYSDATE
	)
