mapper.select:
/* loginProcessing 로그인 결과 조회 */
	SELECT	COUNT(*) CNT
	FROM	MEMBER
	WHERE 	ID = #{userId}
	AND		PASSWORD = #{password}

mapper.insert:
/* insertUser 계정 생성 */
	INSERT
	INTO	MEMBER
	(		ID
	,		PASSWORD
	,		NAME
	,		DATE_OF_BIRTH
	,		PHONE_NUMBER
	,		EMAIL
	)		VALUES
	(		#{userId}
	,		#{password}
	,		#{userName}
	,		#{birth}
	,		#{phoneNum}
	,		#{email}
	)
