mapper.select:
/* popupCnt 팝업 리스트 갯수 조회  */
SELECT	COUNT(*) CNT
FROM	POPUP

mapper.sql:
/* popupWhere 팝업 조회조건  */


		AND 	POPUP_ID = #{popupId}


		AND 	POPUP_TITLE LIKE '%' || #{popupTitle} || '%'


		AND 	USE_YN = #{useYn}

mapper.insert:
/* savePopup 팝업 저장 (신규, 수정)  */
MERGE
INTO	POPUP P
USING	DUAL
ON 		(P.POPUP_ID = #{popupId})
WHEN 	MATCHED THEN
UPDATE
SET		POPUP_TITLE		= #{popupTitle}
,		POPUP_STATUS	= #{popupStatus}
,		POPUP_CONTENT	= #{popupContent}
,		POPUP_IMAGE_NO	= #{popupImageNo}
,		POPUP_URL		= #{popupUrl}
,		USE_YN			= #{useYn}
,		UPDATE_DATE		= #{updateDate}
,		UPDATE_USER		= #{updateUser}
WHEN 	NOT MATCHED THEN
INSERT
(
	POPUP_ID
,	POPUP_TITLE
,	POPUP_STATUS
,	POPUP_CONTENT
,	POPUP_IMAGE_NO
,	POPUP_URL
,	USE_YN
,	CREATE_DATE
,	CREATE_USER
,	UPDATE_DATE
,	UPDATE_USER
)
VALUES
(
	(
		SELECT	NVL(MAX(POPUP_ID), 0) + 1
		FROM	POPUP
	)
,	#{popupTitle}
,	#{popupStatus}
,	#{popupContent}
,	#{popupImageNo}
,	#{popupUrl}
,	#{useYn}
,	#{createDate}
,	#{createUser}
,	#{updateDate}
,	#{updateUser}
)

mapper.delete:
/* deletePopup 팝업 삭제  */
DELETE
FROM	POPUP
WHERE 	POPUP_ID IN

	#{idx}
